/* Стили для блоков кода на Google Gemini - кнопки копирования в верхнем правом углу */
.syntax-highlighted-code-wrapper {
  /* Добавляем относительное позиционирование для родительского контейнера */
  position: relative;
}

.syntax-highlighted-code-wrapper footer {
  /* Перемещаем футер в верхний правый угол */
  position: absolute;
  top: 4px;
  right: 4px;
  /* Полупрозрачный фон для контраста */
  background-color: rgba(36, 38, 41, 0.7);
  border-radius: 4px;
  /* Убираем лишние отступы */
  margin: 0;
  padding: 2px;
  /* Поднимаем над содержимым */
  z-index: 10;
  /* Уменьшаем размер и сжимаем элементы */
  display: flex;
  width: auto;
}

/* Скрываем ненужные текстовые элементы */
.syntax-highlighted-code-wrapper footer .disclaimer,
.syntax-highlighted-code-wrapper footer .spacer,
.syntax-highlighted-code-wrapper footer .language {
  display: none;
}

/* Уменьшаем кнопки и отступы */
.syntax-highlighted-code-wrapper footer .mat-mdc-icon-button {
  width: 28px;
  height: 28px;
  padding: 2px;
}

/* Уменьшаем размер иконок для компактности */
.syntax-highlighted-code-wrapper footer .material-symbols-outlined {
  font-size: 18px;
  color: #e8eaed;
}

/* Эффект при наведении для лучшей интерактивности */
.syntax-highlighted-code-wrapper footer .mat-mdc-icon-button:hover {
  background-color: rgba(255, 255, 255, 0.2);
}

/* Корректируем область нажатия */
.syntax-highlighted-code-wrapper footer .mat-mdc-button-touch-target {
  position: absolute;
  top: 0;
  left: 0;
  height: 100%;
  width: 100%;
  transform: none;
}

/* Добавляем немного отступа для кода, чтобы кнопки не перекрывали */
.syntax-highlighted-code-wrapper .syntax-highlighted-code {
  padding-top: 6px;
}

